        // Complete the bonAppetit function below.    static void bonAppetit(List<int> bill, int k, int b)    {        int sum = 0 ;        for (int i = 0; i < bill.Count; i++)        {            if (i != k)            {                sum += bill[i];            }        }        int bActual = sum / 2;        if (b == bActual)        {            Console.WriteLine("Bon Appetit");        }        else        {            Console.WriteLine(b - bActual);        }    }